import React, { Component } from 'react';
import { Button } from 'antd';
import { Redirect } from 'react-router-dom';
// import Router from './router';


class Show extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toB: false,
      toC: false
    }
  }
  cClick = () => {
    // Router.isLogin = true;
    // alert('已登陆')
    // this.props.history.push('/b/test')
    this.setState({
      toC: true
    })

  }
  bClick = () => {
    // this.props.history.push('/b')
    this.setState({
      toB: true
    })

  }
  render() {
    console.log(this.props)
    if (this.state.toB) {
      return <Redirect to='/b' />
    } else if (this.state.toC) {
      return <Redirect to='/b/test' />
    }
    return (
      <div history={this.props.history}>
        <Button onClick={() => { this.bClick() }} >显示b.js的内容</Button>
        <Button onClick={() => { this.cClick() }} >显示c.js的内容</Button>
        {this.props.children}
      </div>
    )
  }
}

export default Show;
