import React, { Component } from 'react';
import { Route, Switch, HashRouter, Redirect, BrowserHistory } from 'react-router-dom';

import B from './b';
import C from './c';
import Show from './show';
import Home from './home';


let isLogin = false
class Router extends Component {

  render() {
    // const Condition = ({ children }) => { isLogin ? children : <Redirect to='/b' /> }
    // const PrivateRoute = ({ children, ...path }) => {
    //   return (
    //     <Route {...path} render={() => { <Condition children={children} /> }} />

    //   )
    // }
    return (
      <BrowserHistory>
        <Switch>

          <Route exact path='/' component={Home} />
          <Show history={this.props.history}>
            <Route exact path='/b' component={B} />
            <Route exact path='/b/test' component={C} />
          </Show>
          {/* <PrivateRoute path='/check'>
            '已登陆'
          </PrivateRoute> */}
        </Switch>
      </BrowserHistory>
    )
  }
}

export default Router;
